package UserRegistration;

import java.util.regex.Pattern;

public class UserRegistration {
	public boolean validateName(String name) {
		Pattern pattern;
		pattern = Pattern.compile("^[A-Z][a-z]{2,}");
		return pattern.matcher(name).matches();
	}
}
